import React from 'react';

import { withApollo } from '../libs/apollo';
// import { useQuery } from '@apollo/react-hooks';
import { useQuery } from '@apollo/client';
import GraphiQL from 'graphiql';
import { ALL_CONSULTS } from '../gql/allConsults';

import 'graphiql/graphiql.min.css';

const Home = () => {

  const URL = 'https://staging.api.upacaipalmas.com/developers/graphiql';

  function graphQLFetcher(graphQLParams: any) {
    return fetch(URL, {
      method: 'get',
      headers: { "Content-Type": "application/json "},
      body: JSON.stringify(graphQLParams)
    }).then(
      response => response.json()
    )
  }

  const defaultQuery = `
  query {
    companies {
      id
    }
  }

  `;

  const { loading, error, data } = useQuery(ALL_CONSULTS);

    if (error) {
      return <h1>Error</h1>
    }

    if (loading) {
      return <h1>Loading...</h1>
    }

  return(
    <>
      <h1>Nextjs, Apollo and GraphQl setup</h1>

      <div>
        {
          data.characters.results.map( data => (
            <ul key={data.id}>
              <li>{data.id}</li>
            </ul>
          ))
        }
      </div>

      <GraphiQL fetcher={graphQLFetcher} defaultQuery={defaultQuery} />
    </>
    );
}

export default withApollo({ ssr: true })(Home);
